import{_ as s,a,o as i,ag as t}from"./chunks/framework.Do1Zayaf.js";const k=JSON.parse('{"title":"HPCC Systems 8.6.x Releases","description":"","frontmatter":{},"headers":[],"relativePath":"devdoc/red_book/HPCC-Systems-8.6.x-Releases.md","filePath":"devdoc/red_book/HPCC-Systems-8.6.x-Releases.md","lastUpdated":1771585025000}'),o={name:"devdoc/red_book/HPCC-Systems-8.6.x-Releases.md"};function r(n,e,l,h,d,p){return i(),a("div",null,e[0]||(e[0]=[t('<h1 id="hpcc-systems-8-6-x-releases" tabindex="-1">HPCC Systems 8.6.x Releases <a class="header-anchor" href="#hpcc-systems-8-6-x-releases" aria-label="Permalink to &quot;HPCC Systems 8.6.x Releases&quot;">​</a></h1><p>Welcome to the Red Book for HPCC Systems® 8.6.x series.</p><p>You may benefit from glancing at other Red Book entries when making a large jump between releases.</p><p>Here&#39;s how to contact us if you find an issue or want to add something to the Red Book:</p><ul><li>To raise an issue, use our <a href="https://hpccsystems.atlassian.net/jira/" target="_blank" rel="noreferrer">Community Issue Tracker</a>. Please create yourself an account if you don&#39;t already have one, to get automatic updates as your issue progresses through the workflow.</li><li>To ask a developer a technical question about something you are doing or have encountered, post in the Forums on Stack Overflow. We invite you to post your questions on Stack Overflow utilizing the tag <strong>hpcc-ecl</strong> (<a href="https://stackoverflow.com/search?tab=newest&amp;q=hpcc-ecl" target="_blank" rel="noreferrer">StackOverflow</a>).</li><li>To add a note to the RedBook, please contact <a href="mailto:james.defabia@lexisnexisrisk.com" target="_blank" rel="noreferrer">Jim DeFabia</a> with full details.</li></ul><hr><h2 id="embedded-c-using-the-libz-library" tabindex="-1">Embedded C++ using the libz library <a class="header-anchor" href="#embedded-c-using-the-libz-library" aria-label="Permalink to &quot;Embedded C++ using the libz library&quot;">​</a></h2><p>In 8.6 there is a change that affects embedded C++ code using routines from the libz library, such as the <code>uncompress()</code> routine.</p><p>Prior to 8.6, the ECL would compile and run. Using version 8.6 or later, you may get an error similar to the one below about <code>uncompress()</code> or any other libz routine used as &quot;unresolved symbols&quot;:</p><div class="language-cpp vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">cpp</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">Error loading </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">var</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">lib</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">HPCCSystems</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">queries</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/&lt;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">thorname</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">libW20220728</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">-</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">123456.</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">so</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: undefined symbol: uncompress</span></span></code></pre></div><p>The solution is to provide an additional line into the embedded C++ code:</p><div class="language-cpp vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">cpp</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">#option library </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;z&#39;</span></span></code></pre></div>',12)]))}const u=s(o,[["render",r]]);export{k as __pageData,u as default};
