import{_ as a,a as t,o as s,ag as r}from"./chunks/framework.Do1Zayaf.js";const m=JSON.parse('{"title":"HPCC Systems 9.10.x Releases","description":"","frontmatter":{},"headers":[],"relativePath":"devdoc/red_book/HPCC-Systems-9.10.x-Releases.md","filePath":"devdoc/red_book/HPCC-Systems-9.10.x-Releases.md","lastUpdated":1771585025000}'),o={name:"devdoc/red_book/HPCC-Systems-9.10.x-Releases.md"};function i(n,e,l,d,c,u){return s(),t("div",null,e[0]||(e[0]=[r('<h1 id="hpcc-systems-9-10-x-releases" tabindex="-1">HPCC Systems 9.10.x Releases <a class="header-anchor" href="#hpcc-systems-9-10-x-releases" aria-label="Permalink to &quot;HPCC Systems 9.10.x Releases&quot;">​</a></h1><p>Welcome to the Red Book for HPCC Systems® 9.10.x series.</p><p>You may benefit from glancing at other Red Book entries when making a large jump between releases.</p><p>Here&#39;s how to contact us if you find an issue or want to add something to the Red Book:</p><ul><li>To raise an issue, use our <a href="https://hpccsystems.atlassian.net/jira/" target="_blank" rel="noreferrer">Community Issue Tracker</a>. Please create yourself an account if you don&#39;t already have one, to get automatic updates as your issue progresses through the workflow.</li><li>To ask a developer a technical question about something you are doing or have encountered, post in the Forums on Stack Overflow. We invite you to post your questions on Stack Overflow utilizing the tag <strong>hpcc-ecl</strong> (<a href="https://stackoverflow.com/search?tab=newest&amp;q=hpcc-ecl" target="_blank" rel="noreferrer">StackOverflow</a>).</li><li>To add a note to the RedBook, please contact <a href="mailto:james.defabia@lexisnexisrisk.com" target="_blank" rel="noreferrer">Jim DeFabia</a> with full details.</li></ul><hr><h2 id="simplified-installers-for-bare-metal" tabindex="-1">Simplified installers for bare metal <a class="header-anchor" href="#simplified-installers-for-bare-metal" aria-label="Permalink to &quot;Simplified installers for bare metal&quot;">​</a></h2><p>Before 9.10, you had the flexibility to choose and install plugins on an à la carte basis.<br> We&#39;ve listened to your feedback and understand that managing individual plugins could be cumbersome.<br> By integrating them directly into a single platform installer, we aim to provide a more streamlined experience.</p><p>If you have a previous version of the bare metal platform installed with optional plugins, you may need to uninstall the plugins individually.</p><hr><h2 id="rowservice-issue" tabindex="-1">Rowservice issue <a class="header-anchor" href="#rowservice-issue" aria-label="Permalink to &quot;Rowservice issue&quot;">​</a></h2><p>We have identified a vulnerability in the Rowservice in versions 9.10.x through 9.10.14 of the platform<br> that may cause the service to crash under certain conditions.</p><p>To ensure the stability and security of your systems, we strongly recommend upgrading to version 9.10.16 or later<br> to mitigate the risk of crashes and ensure continued reliable operation.</p><p>If you are using earlier minor versions of the platform, you should upgrade to 9.6.84 or later or 9.8.56 or later<br> (<a href="https://hpccsystems.atlassian.net/browse/HPCC-33381" target="_blank" rel="noreferrer">HPCC-33381</a>).</p>',14)]))}const p=a(o,[["render",i]]);export{m as __pageData,p as default};
