import{_ as t,a,o as n,ag as r}from"./chunks/framework.Do1Zayaf.js";const u=JSON.parse('{"title":"HPCC : 10.0 Platform Release Announcement","description":"","frontmatter":{},"headers":[],"relativePath":"devdoc/release_announcements/10.0-Platform-Release-Announcement.md","filePath":"devdoc/release_announcements/10.0-Platform-Release-Announcement.md","lastUpdated":1771585025000}'),i={name:"devdoc/release_announcements/10.0-Platform-Release-Announcement.md"};function o(s,e,c,l,d,h){return n(),a("div",null,e[0]||(e[0]=[r('<h1 id="hpcc-10-0-platform-release-announcement" tabindex="-1">HPCC : 10.0 Platform Release Announcement <a class="header-anchor" href="#hpcc-10-0-platform-release-announcement" aria-label="Permalink to &quot;HPCC : 10.0 Platform Release Announcement&quot;">​</a></h1><p>We&#39;re excited to announce the latest release of the HPCC Platform, packed with significant enhancements that streamline your data analytics workflow and boost productivity. This release focuses on reducing cloud costs, improving user experience, strengthening security, and expanding ECL language capabilities.</p><h2 id="what-s-new-in-10-0" tabindex="-1">What&#39;s New in 10.0 <a class="header-anchor" href="#what-s-new-in-10-0" aria-label="Permalink to &quot;What&#39;s New in 10.0&quot;">​</a></h2><h3 id="enhanced-ecl-watch-interface" tabindex="-1">Enhanced ECL Watch Interface <a class="header-anchor" href="#enhanced-ecl-watch-interface" aria-label="Permalink to &quot;Enhanced ECL Watch Interface&quot;">​</a></h3><ul><li><strong>New Activities Page</strong>: Navigate and monitor your platform activities with our redesigned activities dashboard, providing better visibility into system operations and job status. <em>(Tech Preview)</em></li></ul><h3 id="xref" tabindex="-1">XREF <a class="header-anchor" href="#xref" aria-label="Permalink to &quot;XREF&quot;">​</a></h3><ul><li>XREF now supports containerized deployments.</li></ul><h3 id="strengthened-security-authentication" tabindex="-1">Strengthened Security &amp; Authentication <a class="header-anchor" href="#strengthened-security-authentication" aria-label="Permalink to &quot;Strengthened Security &amp; Authentication&quot;">​</a></h3><ul><li><strong>Improved LDAP Integration</strong>: Enhanced LDAP authentication mechanisms with improved error handling and connection stability.</li><li><strong>Security Fixes</strong>: Multiple security improvements to ensure your data remains protected and compliant with enterprise standards.</li></ul><h3 id="ecl-language-enhancements" tabindex="-1">ECL Language Enhancements <a class="header-anchor" href="#ecl-language-enhancements" aria-label="Permalink to &quot;ECL Language Enhancements&quot;">​</a></h3><ul><li><p><strong>New <code>REGEXEXTRACT</code> Keyword</strong>: Powerful new ECL function for advanced pattern matching and text extraction, enabling more sophisticated data parsing capabilities. For more details, see: <a href="https://hpccsystems.com/wp-content/uploads/_documents/ECLR_EN_US/REGEXEXTRACT.html" target="_blank" rel="noreferrer">REGEXEXTRACT Docs</a></p></li><li><p><strong>Modernized Cryptography</strong>: Replaced the legacy <code>Std.Crypto</code> library with the robust <code>Std.OpenSSL</code> library, providing better performance, enhanced security algorithms, and improved maintainability. For more details, see: <a href="https://hpcc-systems.github.io/HPCC-Platform/devdoc/userdoc/Migrating-from-StdCrypto-to-StdOpenSSL.html" target="_blank" rel="noreferrer">OpenSSL Migration Guide</a></p></li></ul><h3 id="performance-reliability-improvements" tabindex="-1">Performance &amp; Reliability Improvements <a class="header-anchor" href="#performance-reliability-improvements" aria-label="Permalink to &quot;Performance &amp; Reliability Improvements&quot;">​</a></h3><ul><li>Numerous cost reduction and cost tracking enhancements.</li><li>Enhanced system stability across Thor, Roxie, and Dali components.</li><li>Optimized memory management and resource utilization.</li><li>Improved error handling and logging throughout the platform.</li></ul><h2 id="get-started-today" tabindex="-1">Get Started Today <a class="header-anchor" href="#get-started-today" aria-label="Permalink to &quot;Get Started Today&quot;">​</a></h2><p>Ready to experience these exciting new features? Download the latest release and explore how these enhancements can accelerate your data analytics projects. We encourage you to try the new ECL Watch activities page and experiment with the <code>REGEXEXTRACT</code> functionality in your ECL code.</p>',15)]))}const p=t(i,[["render",o]]);export{u as __pageData,p as default};
