SetFuncLib := SERVICE
  INTEGER SumInt(SET OF INTEGER ss) :
     holertl,library='dab',entrypoint='rtlSumInt';
END;
x:= 3+4.5;
SetFuncLib.SumInt([x, 11.79]); //passed two REAL numbers - it works

__int64 rtlSumInt(unsigned len, __int64 * a) {
     __int64 sum = 0;
     for(unsigned i = 0; i < len; i++) {
          sum += a[i];
     }
     return sum;
  }

