sect1.programlisting:
SequencedAges := RECORD
        Ages;
        INTEGER8 Sequence := 0;
END;

SequencedAges AddSequence(Ages l, INTEGER c) := TRANSFORM
        SELF.Sequence := IF(c % 2 = 0, SKIP,c); //skip the even recs
        SELF := l;
END;

SequencedAgedRecs := PROJECT(AgedRecs, AddSequence(LEFT,COUNTER));
