MaxVal2 := MAX(4,8,16,2,1); 
SetVals := [4,8,16,2,1];
MaxVal3 := MAX(SetVals); 

OUTPUT(MaxVal2); //returns 16
OUTPUT(MaxVal3); //returns 16

//example using a DATASET
SalesRecord := RECORD
  INTEGER OrderNumber;
  INTEGER SaleAmount;
END;
Sales := DATASET([{923,1001},
                  {924,23},
                  {925,3000},
                  {926,3423},
                  {927,9999},
                  {931,113}], SalesRecord);

OUTPUT(MAX(Sales,Sales.SaleAmount)); //returns 9999
