sect1.programlisting:
#OPTION('pickBestEngine', 0);
IMPORT std;
IMPORT DataConnectors;
Layout_Flat := RECORD
  STRING10  fname;
  STRING10  lname;
  UNSIGNED1 prange;
  STRING10  street;
  UNSIGNED1 zips;
  UNSIGNED1 age;
  STRING2   birth_state;
  STRING3   birth_month;
  UNSIGNED1 one;
  UNSIGNED8 id;
END;
DataConnectors.HDFSConnector.PipeIn(MyDataFile, 
                           '/user/hadoop/test/MyData1', 
                           Layout_Flat, FLAT, 
                           '192.168.56.120', 
                           54310);
OUTPUT(MyDataFile);

#OPTION('pickBestEngine', 0);
IMPORT std;
IMPORT DataConnectors;
Layout_CSV := RECORD
  STRING10 fname;
  STRING10 lname;
  STRING3  prange;
  STRING10 street;
  STRING5  zips;
  STRING3  age;
  STRING2  birth_state;
  STRING3  birth_month;
  STRING3  one;
  STRING20 id;
END;
DataConnectors.HDFSConnector.PipeIn(MyDataFile, 
                           '/user/Administrator/test/MyData1', 
                           Layout_CSV, CSV(SEPARATOR('|')), 
                           '192.168.56.120', 
                           54310);
OUTPUT(MyDataFile);
