sect1.programlisting:
#OPTION('pickBestEngine', 0);
IMPORT std;
IMPORT DataStream;
Layout_Flat := RECORD
  STRING10  fname;
  STRING10  lname;
  UNSIGNED1 prange;
  STRING10  street;
  UNSIGNED1 zips;
  UNSIGNED1 age;
  STRING2   birth_state;
  STRING3   birth_month;
  UNSIGNED1 one;
  UNSIGNED8 id;
END;
DataStream.HDFSPipe.PipeIn(certrecords, 
                           '/user/hadoop/test/cert1', 
                           Layout_Flat, 
                           FLAT, 
                           '192.168.56.120', 
                           54310)

#OPTION('pickBestEngine', 0);
IMPORT std;
IMPORT DataStream;
Layout_CSV := RECORD
  STRING10 fname;
  STRING10 lname;
  STRING3  prange;
  STRING10 street;
  STRING5  zips;
  STRING3  age;
  STRING2  birth_state;
  STRING3  birth_month;
  STRING3  one;
  STRING20 id;
END;
DataStream.HDFSPipe.PipeIn(certrecords, 
                           '/user/Administrator/test/cert1', 
                           Layout_CSV, 
                           CSV(SEPARATOR('|')), 
                           '192.168.56.120', 
                           54310)
