sect1.programlisting:
wuid := WORKUNIT //get WUID

namesRecord := RECORD
STRING20 surname;
STRING10 forename;
INTEGER2 age;
END;

namesTable := DATASET([
{'Halligan','Kevin',31},
{'Halligan','Liz',30},
{'Salter','Abi',10},
{'X','Z'}], namesRecord);

DISTRIBUTION(namesTable, surname, forename,
        NAMED('Stats'));
x := DATASET(ROW(TRANSFORM({STRING line},
SELF.line := WORKUNIT('Stats', STRING))));
