OldSort := SORT(Person,Person.per_first_name) : DEPRECATED('Use NewSort instead.');
NewSort := SORT(Person,-Person.per_first_name);

OUTPUT(OldSort); 
//produces this warning:
// Attribute OldSort is marked as deprecated. Use NewSort instead.

