IMPORT Visualizer;
  _usStates := DATASET([  {'AL', 4779736}, 
                         {'AK', 710231}, 
                         {'AZ', 6392017}, 
                         {'AR', 2915918}],
                         {STRING State, INTEGER4 weight});
  data_usStates := OUTPUT(_usStates, NAMED('choro_usStates'));
  data_usStates;
  viz_usstates := Visualizer.Choropleth.USStates('usStates',, 'choro_usStates');
  viz_usstates;

